// src/components/TimeTable.js
import React, { useState } from "react";
import "./TimeTable.css";

// 05:00 ~ 24:00 30분 단위 슬롯 레이블 생성
const hours = Array.from({ length: 39 }, (_, i) => {
  const totalMin = 300 + i * 30;
  const h = Math.floor(totalMin / 60);
  const m = totalMin % 60;
  return `${String(h).padStart(2, "0")}:${String(m).padStart(2, "0")}`;
});

function TimeTable({ reservations, onCancel, currentTime, viewedDate }) {
  // 현재 시각, 오늘 여부, 분 단위
  const now = new Date(currentTime);
  const nowMinutes = now.getHours() * 60 + now.getMinutes();
  const isToday = viewedDate === now.toISOString().split("T")[0];

  // 각 예약의 시작/끝 인덱스 계산
  const groups = reservations.map(r => {
    const startIndex = hours.indexOf(r.start);
    const endIndex = hours.indexOf(r.end);
    return { ...r, startIndex, endIndex };
  });

  // 모달 상태
  const [isModalOpen, setModalOpen] = useState(false);
  const [selectedRes, setSelectedRes] = useState(null);
  const [passwordInput, setPasswordInput] = useState("");
  const [error, setError] = useState("");

  const openCancelModal = r => {
    setSelectedRes(r);
    setPasswordInput("");
    setError("");
    setModalOpen(true);
  };
  const closeModal = () => setModalOpen(false);
  const confirmCancel = () => {
    if (passwordInput === selectedRes.password) {
      onCancel(selectedRes.id);
      closeModal();
    } else {
      setError("비밀번호가 일치하지 않습니다.");
    }
  };

  return (
    <>
      <div className="timetable">
        {hours.map((slot, idx) => {
          // 이 슬롯이 속한 예약 그룹 찾기
          const group = groups.find(
            g => idx >= g.startIndex && idx < g.endIndex
          );
          const isReserved = !!group;
          const isStart = isReserved && idx === group.startIndex;
          const isEnd = isReserved && idx === group.endIndex - 1;

          // 과거 슬롯 여부 판정
          const [h, m] = slot.split(":").map(Number);
          const slotMin = h * 60 + m;
          const isPast = isToday && slotMin < nowMinutes;

          // CSS 클래스 조합
          const classNames = [
            "time-slot",
            isReserved && "reserved-slot",
            isStart && "reserved-start",
            isEnd && "reserved-end",
            isPast && "past"
          ]
            .filter(Boolean)
            .join(" ");

          return (
            <div
              key={slot}
              className={classNames}
              onClick={() =>
                isStart && !isPast && openCancelModal(group)
              }
            >
              <div className="slot-time">{slot}</div>
              <div className="slot-info">
                {isReserved ? (
                  isStart ? (
                    <>
                      <span>{group.user}</span>
                      <button
                        className="cancel"
                        onClick={e => {
                          e.stopPropagation();
                          openCancelModal(group);
                        }}
                        disabled={isPast}
                      >
                        취소
                      </button>
                    </>
                  ) : null
                ) : (
                  <span className="available">
                    {isPast ? "지나감" : "예약 가능"}
                  </span>
                )}
              </div>
            </div>
          );
        })}
      </div>

      {isModalOpen && (
        <div className="modal-backdrop">
          <div className="cancel-modal">
            <h3>예약 취소</h3>
            <p>
              {selectedRes.user} 님의 예약을 취소하려면 비밀번호를 입력하세요.
            </p>
            <input
              type="password"
              value={passwordInput}
              onChange={e => setPasswordInput(e.target.value)}
              placeholder="4자리 비밀번호"
            />
            {error && <div className="error">{error}</div>}
            <div className="modal-buttons">
              <button onClick={confirmCancel}>확인</button>
              <button onClick={closeModal}>취소</button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default TimeTable;
