// src/components/TimeTable.js
import React from "react";
import { rooms, slots } from "../utils/data";


export default function TimeTable({ reservations, onCancel }) {
  return (
    <table style={{ width: "100%", borderCollapse: "collapse" }}>
      <thead>
        <tr>
          <th style={{ border: "1px solid #ccc" }}>방/시간</th>
          {slots.map(time => (
            <th key={time} style={{ border: "1px solid #ccc" }}>{time}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {rooms.map(room => (
          <tr key={room}>
            <td style={{ border: "1px solid #ccc", fontWeight: "bold" }}>{room}</td>
            {slots.map(time => {
              const slot = reservations.find(r => r.room === room && r.start === time);
              return (
                <td
                  key={time}
                  style={{
                    border: "1px solid #eee",
                    padding: "4px",
                    background: slot ? "#d1e7dd" : "#f8f9fa"
                  }}
                >
                  {slot ? (
                    <>
                      <div>{slot.user}</div>
                      <button
                        style={{
                          marginTop: "4px",
                          padding: "2px 6px",
                          fontSize: "0.8em"
                        }}
                        onClick={() => onCancel(slot.id)}
                      >
                        취소
                      </button>
                    </>
                  ) : (
                    "—"
                  )}
                </td>
              );
            })}
          </tr>
        ))}
      </tbody>
    </table>
  );
}
