/* src/App.js */
import React, { useState, useEffect } from "react";
import TimeTable from "./components/TimeTable";
import ReserveForm from "./components/ReserveForm";
import { getInitialReservations } from "./utils/data";
import './App.css';

function App() {
  const [reservations, setReservations] = useState(getInitialReservations());
  const [now, setNow] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => setNow(new Date()), 60000);
    return () => clearInterval(timer);
  }, []);

  const handleReserve = newRes =>
    setReservations(prev => [...prev, newRes]);

  const handleCancel = id =>
    setReservations(prev => prev.filter(r => r.id !== id));

  return (
    <div className="container">
      <header>
        <h1>스크린골프 예약</h1>
        <div className="now">{now.toLocaleTimeString('ko-KR')}</div>
      </header>
      <TimeTable reservations={reservations} onCancel={handleCancel} currentTime={now} />
      <ReserveForm reservations={reservations} onReserve={handleReserve} />
    </div>
  );
}

export default App;
