/* src/components/TimeTable.js */
import React from "react";
import { rooms, slots } from "../utils/data";

export default function TimeTable({ reservations, onCancel, currentTime }) {
  const currentHour = currentTime.getHours() + currentTime.getMinutes() / 60;
  const centerHour = currentHour - 3;
  const centerIdx = slots.findIndex(s => {
    const [h, m] = s.split(":").map(Number);
    return h + m / 60 >= centerHour;
  });

  return (
    <div style={{ overflowX: "auto" }}>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th style={{ border: "1px solid #ccc" }}>시간</th>
            {slots.map(time => (
              <th key={time} style={{ border: "1px solid #ccc" }}>{time}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {rooms.map(room => (
            <tr key={room}>
              <td style={{ border: "1px solid #ccc", fontWeight: "bold" }}>{room}</td>
              {slots.map((time, idx) => {
                const slot = reservations.find(r => {
                  const startIdx = slots.indexOf(r.start);
                  const endIdx = slots.indexOf(r.end);
                  return r.room === room && idx >= startIdx && idx < endIdx;
                });
                if (slot) {
                  const isStart = time === slot.start;
                  return (
                    <td
                      key={time}
                      style={{
                        border: "1px solid #eee",
                        padding: "4px",
                        background: "#d1e7dd"
                      }}
                    >
                      {slot.user}
                      {isStart && (
                        <button
                          style={{ marginLeft: "8px", padding: "2px 6px", fontSize: "0.8em" }}
                          onClick={() => onCancel(slot.id)}
                        >
                          취소
                        </button>
                      )}
                    </td>
                  );
                }
                return (
                  <td
                    key={time}
                    style={{ border: "1px solid #eee", padding: "4px", background: "#f8f9fa" }}
                  >
                    —
                  </td>
                );
              })}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
