import React from "react";
import "./TimeTable.css";
import { slots } from "../utils/data";

function TimeTable({ reservations }) {
  return (
    <div style={{ border: "1px solid #ccc", borderRadius: "12px", overflow: "hidden", marginTop: "16px" }}>
      {slots.map((slot, idx) => {
        const res = reservations.find(r => r.start === slot);
        const isReserved = !!res;
        const style = {
          backgroundColor: idx % 2 === 0 ? "#f8f8f8" : "#ffffff",
          borderBottom: "1px solid #ddd",
          padding: "8px 12px",
          fontSize: "1rem",
          fontWeight: isReserved ? 600 : 400,
          color: isReserved ? "#d93025" : "#333",
          display: "flex",
          justifyContent: "space-between"
        };
        return (
          <div key={slot} style={style}>
            <span>{slot}</span>
            {isReserved && <span>{res.room}í˜¸ {res.user}</span>}
          </div>
        );
      })}
    </div>
  );
}

export default TimeTable;
