/* src/components/TimeTable.js */
import React from "react";
import { rooms, slots } from "../utils/data";
import './TimeTable.css';

export default function TimeTable({ reservations, onCancel, currentTime }) {
  const currentHour = currentTime.getHours() + currentTime.getMinutes()/60;
  // compute center offset index = index of slot matching currentHour - 3
  const centerHour = currentHour - 3;
  const centerIdx = slots.findIndex(s => {
    const [h, m] = s.split(':').map(Number);
    return h + m/60 >= centerHour;
  });

  return (
    <div className="table-wrapper">
      <table className="timetable">
        <thead>
          <tr>
            <th>시간</th>
            {slots.map(time => (
              <th key={time}>{time}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {rooms.map(room => (
            <tr key={room}>
              <td className="room-cell">{room}</td>
              {slots.map((time, idx) => {
                const slot = reservations.find(r => {
                  const startIdx = slots.indexOf(r.start);
                  const endIdx = slots.indexOf(r.end);
                  return r.room===room && idx>=startIdx && idx<endIdx;
                });
                return (
                  <td key={time} className={slot ? 'booked' : 'free'}>
                    {slot ? (
                      <>
                        {slot.user}
                        {time===slot.start && (
                          <button className="cancel-btn" onClick={()=>onCancel(slot.id)}>취소</button>
                        )}
                      </>
                    ) : '—'}
                  </td>
                );
              })}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
