// src/components/ReserveForm.js
import React, { useState } from "react";
import { rooms, slots } from "../utils/data";


export default function ReserveForm({ reservations, onReserve }) {
  const [room, setRoom] = useState("1번 방");
  const [start, setStart] = useState("18:00");
  const [user, setUser] = useState("");

  const handleSubmit = e => {
    e.preventDefault();
    // 동일 슬롯 중복 예약 방지
    if (reservations.some(r => r.room === room && r.start === start)) {
      alert("이미 예약된 시간입니다.");
      return;
    }
    onReserve({
      id: `${room}-${start}`,
      room,
      start,
      user
    });
    setUser("");
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginTop: "16px" }}>
      <select value={room} onChange={e => setRoom(e.target.value)}>
        {rooms.map(r => <option key={r} value={r}>{r}</option>)}
      </select>
      <select value={start} onChange={e => setStart(e.target.value)}>
        {slots.map(s => <option key={s} value={s}>{s}</option>)}
      </select>
      <input
        type="text"
        placeholder="이름 입력"
        value={user}
        onChange={e => setUser(e.target.value)}
        required
      />
      <button type="submit" style={{ marginLeft: "8px" }}>예약하기</button>
    </form>
  );
}
