// src/App.js
import React, { useState } from "react";
import TimeTable from "./components/TimeTable";
import ReserveForm from "./components/ReserveForm";
import { getInitialReservations } from "./utils/data";

function App() {
  const [reservations, setReservations] = useState(getInitialReservations());

  const handleReserve = newRes =>
    setReservations(prev => [...prev, newRes]);

  const handleCancel = id =>
    setReservations(prev => prev.filter(r => r.id !== id));

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>스크린골프 예약 시스템</h1>
      <TimeTable reservations={reservations} onCancel={handleCancel} />
      <ReserveForm reservations={reservations} onReserve={handleReserve} />
    </div>
  );
}

export default App;
